#include "camera.h"float angle_deg = 1;float angle_rad = 1.57;float radius = 0.02;vec3 pos = vec3(0, 0.3, 0.0);vec3 view = vec3(0.0, 0.3, 0.02);vec3 next_pnt = vec3(0.0, 0.0, 0.0);vec3 operator*(vec3 v, int d){    float x  = v.x * d;    float y = v.y * d;    float z = v.z * d;    return vec3(x, y, z);}void MoveForward(){    vec3 d = view - pos;    d = d * 2;    next_pnt = pos + d;    pos = view;    view = next_pnt;}void MoveBack(){    vec3 d = view - pos;    next_pnt = pos - d;    view = pos;    pos = next_pnt;}void TurnLeft(){    angle_rad -=0.02;    view.x = pos.x + (radius * cos(angle_rad));    view.z = pos.z + (radius * sin(angle_rad));}void TurnRight(){    angle_rad +=0.02;    view.x = pos.x + (radius * cos(angle_rad));    view.z = pos.z + (radius * sin(angle_rad));}